import Vue from 'vue'
import Vuex from 'vuex'
import vueResource from 'vue-resource'
import { stat } from 'fs';

Vue.use(Vuex)
Vue.use(vueResource)

export default new Vuex.Store({
  state: {
    allAudits:{},
    program: {},
    userDetails: {
      status: false,
    },
    season: {},
    episode: {},
    userList: {},
    singleVideos: {},
    multiVideos: {},
    seasonalVideos: {},
    seasons:{},
    episodes:{},
    userCount:0,
    adminCount:0,
    programCount:0,
    // VK
    userListEdit:{
      editable: false,
    },
    languages:{},
    crewRoles:{},
    categories:{
      name:"Categories",
      id:"category",
      subCategories:[],
    },
    stackOfCategories:[]
  },

  mutations: {
    setAllAudits(state,payload) {
      state.allAudits = payload
    },
    setProgram(state,payload) {
      state.program = payload
    },
    setSeason(state,payload) {
      state.season = payload
    },
