        console.log(resp.body)
        commit('setUserList',resp.body)
      }).catch( (err) => {
      console.log(err)
      })
    },
    addUser : ({commit},payload) => {
      return new Promise ((resolve,reject) => {
        Vue.http.post("http://172.16.20.78:8080/useradmin/addUserAdmin",JSON.stringify(payload)).then( (resp) => {
          resolve(resp.body)
        }).catch( (err) => {
          console.log(err)
          reject(false)
        })
      }) 
