    deleteUser : ({commit},payload) => {
      return new Promise ((resolve,reject) => {
        Vue.http.delete("http://172.16.20.78:8080/useradmin/userDeleteByDetails?idDelete="+payload.idDelete+"&id="+payload.id).then( (resp) => {
          resolve(resp.body)
        }).catch( (err) => {
          console.log(err)
          reject(false)
        })
      })
    },
