import { mapGetters, mapActions } from 'vuex';
export default {
    name:'MultiVideoForm',
    data() {
       return {
           multiVideo: {
               program: {
                   id: ""
               },
               seasonName:"",
               seasonNumber:0,
               seasonDescription:"",
               seasonImgUrls:{
                   thumbnail: "",
                    avatar: "",
               }
           }
       }
    },
    methods: {
        ...mapActions([
            'addSeason',
            'imageUpload'
        ]),
        handleSubmit() {
            this.multiVideo.program.id = this.program.id

            this.addSeason(this.multiVideo).then( (resp) => {
                if(resp) {
                    this.$swal('','Successfully added','success')
                    this.$router.push('/episodeForm')
                } else {
                    console.log("Something went wrong")
                }
            }).catch((err) => {
                console.log(err)
            })
        },
         processFile(event,type) {
            if (type=='Thumbnail') {
                let formData = new FormData()
                formData.append('image', event.target.files[0])
                formData.append('type','Thumbnail')
                this.imageUpload(formData).then( (resp) => {
                    if(resp.uploadLink) {
                        this.multiVideo.seasonImgUrls.thumbnail = resp.uploadLink
                    } else {
                        this.$swal('File type not supported')
                    }
                }).catch( (err) => {
                    console.log(err)
                })
            } else {
                let formData = new FormData()
                formData.append('image', event.target.files[0])
                formData.append('type','avatar')
                this.imageUpload(formData).then( (resp) => {
                    if(resp.uploadLink) {
                        this.multiVideo.seasonImgUrls.avatar = resp.uploadLink
                    } else {
                        this.$swal('File type not supported')
                    }
                }).catch( (err) => {
                    console.log(err)
                })
            }
        }

    },
    computed: {
        ...mapGetters([
            'program'
        ])
    }

}
